SELECT (SUM(L_EXTENDEDPRICE * (1 - L_DISCOUNT)) * 100.0 / (SELECT SUM(L_EXTENDEDPRICE * (1 - L_DISCOUNT)) FROM LINEITEM T1 WHERE L_SHIPDATE BETWEEN '1993-09-01' AND '1993-09-30')) AS revenue_percentage FROM LINEITEM T1 JOIN PART T2 ON T1.L_PARTKEY = T2.P_PARTKEY WHERE T2.P_TYPE LIKE '%PROMO%' AND L_SHIPDATE BETWEEN '1993-09-01' AND '1993-09-30';