SELECT COUNT(DISTINCT S.S_SUPPKEY) FROM SUPPLIER S JOIN NATION N ON S.S_NATIONKEY = N.N_NATIONKEY JOIN PARTSUPP PS ON S.S_SUPPKEY = PS.PS_SUPPKEY JOIN LINEITEM L ON PS.PS_PARTKEY = L.L_PARTKEY WHERE N.N_NAME = 'CANADA' AND L.L_SHIPDATE BETWEEN '1993-01-01' AND '1993-12-31' AND L.L_QUANTITY > 0.5 * PS.PS_AVAILQTY;